package principal;

public class Program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//o laço for é mais utilizada quando precisamos repetir os comandos.
		
		/*sintaxe do for 
		1º-for (variavel que inicia o contador;
		2º-		condição que deve ser aceita;
		3º		incremento)
				*/
		for(int contador = 0; contador < 10; contador++){
			System.out.println("valor sw contador:" +contador);
		}//++ = somar  mais um.
	}

}
